/** Content Type Check All **/
document.addEventListener('DOMContentLoaded', function (event) {
  var checkAllContainer = document.querySelectorAll('.check-all.content-types');
  checkAllContainer.forEach(function (container) {
    var master = container.querySelector('input[type="checkbox"].master');
    var slaves = container.querySelectorAll('.slaves input[type="checkbox"]:not(:disabled)');
    var updateMaster = function updateMaster() {
      var allChecked = container.querySelectorAll('.slaves input[type="checkbox"]:not(:checked)').length == 0;
      master.checked = allChecked;
    };
    master.addEventListener('change', function (elem) {
      var isChecked = elem.target.checked;
      slaves.forEach(function (slave) {
        slave.checked = isChecked;
      });
    });
    slaves.forEach(function (slave) {
      slave.addEventListener('change', function () {
        updateMaster();
      });
    });
    updateMaster();
  });
});