function updateStepOrders(e,t){let n=document.getElementById("stepOrderUrl"),d=document.getElementById("deploymentPlanId"),a=document.querySelector("input[name='__RequestVerificationToken']"),o=document.getElementById("stepOrderErrorMessage"),r=n.dataset.url,s=d.dataset.id;fetch(r,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({__RequestVerificationToken:a.value,id:s,newIndex:t,oldIndex:e})}).then(e=>e.json()).catch(e=>{alert(o.dataset.message)})}document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("stepOrder");e&&Sortable.create(e,{handle:".ui-sortable-handle",onSort:e=>{updateStepOrders(e.oldIndex,e.newIndex)}})});