function updateContentItemOrders(e,t){var n=document.getElementById("ordering-url").dataset.url,a=document.getElementById("container-id").dataset.id,d=document.getElementById("pager-before").dataset.before,r=document.getElementById("pager-after").dataset.after,o=document.getElementById("pager-pagesize").dataset.pagesize,i=document.querySelector("input[name='__RequestVerificationToken']"),m=document.getElementById("update-order-error-message");fetch(n,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({__RequestVerificationToken:i.value,containerId:a,oldIndex:e,newIndex:t,pagerSlimParameters:{before:d,after:r},pageSize:o})}).catch(e=>{alert(m.dataset.message)})}document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("ci-sortable");Sortable.create(e,{handle:".ui-sortable-handle",onSort:function(e){updateContentItemOrders(e.oldIndex,e.newIndex)}})});